(14, 12, 'images/new-cat-dust-free-litter-434w-613h.png', '<h4>Dust-free</h4>\r\n                <p>Helps keep households free of litter dust in the air.</p>', NULL, NULL),
(15, 12, 'images/new-cat-rff-bnr-1800w-1200h.jpg', '<h2>TAKE THE GUESSWORK OUT OF NUTRITION</h2>\r\n                <p>Explore our Right Food Finder flowchart to find the right food for your kitten\'s unique needs.</p>', NULL, NULL),
(17, 12, 'storage/1630188666.jpg', '<h4 class=\"text-center p-2\">Vet services</h4>\r\n                    <p>We are committed to delivering compassionate vet care with a broad range of affordable, personalized solutions. Whatever your pet needs, our vets can help.</p>', NULL, '2021-08-29 04:11:06'),
(18, 12, 'images/new-cat-insurance-1074w-628h (1).jpg', '<h4 class=\"text-center p-2\">Pet insurance</h4>\r\n                    <p>From accidents to illnesses, be ready for anything with insurance coverage that helps ensure you don’t have to choose between quality care for your pet and paying your bill.</p>', NULL, NULL),
